INSERT INTO result(category, number, message, language) VALUES ('personality',1,'You are dynamic, strong-willed and a natural leader, and your single-minded focus sets the stage for your success.','en');
INSERT INTO result(category, number, message, language) VALUES ('personality',2,'You are a peacemaker, nurturing, caring and kind, and an indispensable friend, mate, employee or boss.','en');
