INSERT INTO result(category, number, message, language) VALUES ('character',6,'En tant que parent / enseignant / être humain né humanitaire, orienté vers la communauté et exceptionnel, qui ''fait toujours un effort supplémentaire'', vous êtes un comité exemplaire ou un leader de cause.','fr');
INSERT INTO result(category, number, message, language) VALUES ('character',7,'En tant que génie philosophiquement avant-gardiste et à la recherche de connaissances, vous ne recherchez pas les feux de la rampe, mais vous êtes des enseignants, des professeurs et des spécialistes de carrière exceptionnels.','fr');
