INSERT INTO result(category, number, message, language) VALUES ('character',9,'En tant que sage né, aristocratique, humanitaire socialement agréable, vous vous dirigez vers la communauté qui peut le plus bénéficier de votre mission de ''serveur désintéressé''.','fr');
INSERT INTO result(category, number, message, language) VALUES ('character',11,'En tant que conseiller ou leader communautaire empathique, éclairé et compréhensif, vous êtes sous les feux de la rampe; le 11 est associé à la renommée et à l''illumination. ','fr');
INSERT INTO result(category, number, message, language) VALUES ('character',22,'En tant que bâtisseur d''empires à l''esprit élevé et organisateur à orientation spirituelle, vous êtes le mieux placé pour aider les causes publiques et les entreprises qui apprécient votre style dévoué et franc.','fr');
