INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',1,'Cet agenda suscite un désir inné d''être indépendant, de diriger, d''innover et de créer des concepts et des choses originaux et uniques.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',2,'Cet agenda fait naître le désir inné de vivre la vie dans la paix et l''harmonie, avec une nature non agressive, coopérative et sympathique pour tous.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',3,'Cet agenda inspire un désir inné d''être plus créatif, divertissant et de parcourir le monde, offrant plus de plaisir et de joie à tous ceux qui s''engagent avec votre personnalité édifiante.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',4,'Cet agenda implique logique, organisation et persévérance pour manifester les besoins innés, les désirs et la tranquillité d''esprit que vous hébergez et détenez.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',5,'Cet agenda inspire un désir inné de liberté plus que toute autre chose, tout en acquérant la capacité de voyager et de vivre autant que possible sans retenue.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',6,'Cet agenda a une forte orientation humanitaire innée - aider et prendre soin de toutes les choses de la terre - les humains, les animaux et la belle terre elle-même.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',7,'Cet agenda provoque une soif innée d''acquérir des connaissances associées aux sciences (ésotériques ou non), aux philosophies, aux arts et à la spiritualité.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',8,'Cet agenda exprime un désir inné de pouvoir, d''acclamer et d''amasser d''énormes sommes d''argent, ou d''être un enseignant éclairé des masses.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',9,'Cet agenda affecte un désir inné d''atteindre le meilleur de tout, des vêtements et voitures aux divertissements, ou un besoin démesuré de vivre une vie entièrement humanitaire sans une pensée pour soi.','fr');
INSERT INTO result(category, number, message, language) VALUES ('hidden_agenda',11,'Cet agenda suscite un besoin inné de servir, de conseiller ou de participer aux arts de la guérison d''une manière qui aide le plus grand nombre, ou de se faire connaître pour vos services humanitaires.','fr');
